mapper.select:
SELECT count(1) FROM ZCJ_WLSCRM_SERVERS svr, ZCJ_WLSCRM_THREAD trd, ZCJ_SHSNC_RADIOS rds WHERE svr.ip = trd.ip AND svr.port = trd.port AND rds.name = svr.app
AND svr.iscollect = 'Y'
and rds.system = #{system}
and rds.value = #{value}
and rds.name = #{name}
and (trd.threadrun >=svr.threadthreshold or trd.threadrun = -1 )
AND trd.TIME >= to_date('2015-10-30 11:59:00', 'yyyy-mm-dd hh24:mi:ss') AND trd.TIME <= to_date('2015-11-01 0:00:48', 'yyyy-mm-dd hh24:mi:ss')
and trd.time >= to_date(#{start},'yyyy-mm-dd hh24:mi:ss') and trd.time <=to_date(#{end},'yyyy-mm-dd hh24:mi:ss')

SELECT svr.app, svr.module, svr.threadthreshold, svr.ip, svr.port, svr.server, trd.threadtotal, trd.threadidle, trd.threadrun, rds.value, rds.system, rds.name FROM ZCJ_WLSCRM_SERVERS svr, ZCJ_WLSCRM_THREAD trd, ZCJ_SHSNC_RADIOS rds WHERE svr.ip = trd.ip AND svr.port = trd.port AND rds.name = svr.app
AND svr.iscollect = 'Y'
and rds.system = #{system}
and rds.value = #{value}
and rds.name = #{name}
and (trd.threadrun >=svr.threadthreshold or trd.threadrun = -1 )
AND trd.TIME >= to_date('2015-10-30 11:59:00', 'yyyy-mm-dd hh24:mi:ss') AND trd.TIME <= to_date('2015-11-01 0:00:48', 'yyyy-mm-dd hh24:mi:ss')
and trd.time >= to_date(#{start},'yyyy-mm-dd hh24:mi:ss') and trd.time <=to_date(#{end},'yyyy-mm-dd hh24:mi:ss')
ORDER BY svr.app,svr.module,svr.ip,svr.port
